import Foundation
import AVFoundation

// Temporary stub for NativeAppleTranscriptionService to allow building on current macOS versions
// This preserves the original implementation for future use when macOS 26 is available
class NativeAppleTranscriptionService: TranscriptionService {
    enum ServiceError: Error, LocalizedError {
        case unsupportedOS

        var errorDescription: String? {
            "Native Apple transcription requires macOS 26 or later."
        }
    }

    func transcribe(audioURL: URL, model: any TranscriptionModel) async throws -> String {
        throw ServiceError.unsupportedOS
    }
}

